import React, { useState, useEffect } from 'react';
import '../../App.css';

const backgroundImages = ['image1.jpg', 'image2.jpg', 'image3.jpg']; // Aggiungi qui i tuoi nomi di file delle immagini

export default function SignUp() {
  const [currentImageIndex, setCurrentImageIndex] = useState(0);

  useEffect(() => {
    const intervalId = setInterval(() => {
      // Calcola il prossimo indice dell'immagine
      const nextImageIndex = (currentImageIndex + 1) % backgroundImages.length;
      setCurrentImageIndex(nextImageIndex);
    }, 20000); // Cambia immagine ogni 20 secondi

    return () => {
      clearInterval(intervalId); // Pulisce l'intervallo quando il componente si smonta
    };
  }, [currentImageIndex]);

  const backgroundImage = `url(${backgroundImages[currentImageIndex]})`;

  return (
    <div className='sign-up' style={{ backgroundImage }}>
      <h1>LIKE & SUBSCRIBE</h1>
    </div>
  );
}
