import React from 'react';
import '../../App.css';
import Cards from '../Cards';
import HeroSection from '../HeroSection';
import Footer from '../Footer';

function Home(props) {
  // Controlla se il parametro di query "?highlight=cards" è presente nell'URL
  const isHighlightingCards = props.location.search.includes('highlight=cards');

  return (
    <>
      <HeroSection />
      <Cards className={isHighlightingCards ? 'highlighted-cards' : ''} />
      <Footer />
    </>
  );
}

export default Home;
