import React, { useState } from 'react';
import './SignupForm.css';

export default function SignupForm() {
  const [formData, setFormData] = useState({
    firstName: '',
    lastName: '',
    profession: '',
    birthDate: '',
    email: '',
  });
  const [submittedData, setSubmittedData] = useState(null);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value,
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // Salva i dati del modulo dopo la sottomissione
    setSubmittedData(formData);
  };

  return (
    <div>
      {submittedData ? (
        <div className="submitted-data">
          <h2>Dati registrati:</h2>
          <ul>
            {Object.keys(submittedData).map((key) => (
              <li key={key}>
                {key}: {submittedData[key]}
              </li>
            ))}
          </ul>
        </div>
      ) : (
        <form className="signup-form" onSubmit={handleSubmit}>
          <h2 className="form-title">Registrazione</h2>
          <div className="form-group">
            <label htmlFor="firstName">Nome:</label>
            <input
              type="text"
              id="firstName"
              name="firstName"
              value={formData.firstName}
              onChange={handleInputChange}
              required
              className="input-field"
            />
          </div>
          <div className="form-group">
            <label htmlFor="lastName">Cognome:</label>
            <input
              type="text"
              id="lastName"
              name="lastName"
              value={formData.lastName}
              onChange={handleInputChange}
              required
              className="input-field"
            />
          </div>
          <div className="form-group">
            <label htmlFor="profession">Professione:</label>
            <input
              type="text"
              id="profession"
              name="profession"
              value={formData.profession}
              onChange={handleInputChange}
              required
              className="input-field"
            />
          </div>
          <div className="form-group">
            <label htmlFor="birthDate">Data di Nascita:</label>
            <input
              type="date"
              id="birthDate"
              name="birthDate"
              value={formData.birthDate}
              onChange={handleInputChange}
              required
              className="input-field"
            />
          </div>
          <div className="form-group">
            <label htmlFor="email">Email:</label>
            <input
              type="email"
              id="email"
              name="email"
              value={formData.email}
              onChange={handleInputChange}
              required
              className="input-field"
            />
          </div>
          <button type="submit" className="submit-button">Registrati</button>
        </form>
      )}
    </div>
  );
}
